// Použijeme $input.all(), což vezme všechna data, která do tohoto uzlu přišla
// Pokud jsou data prázdná (testování bez vstupu), vytvoříme prázdný objekt, aby to nepadlo
const trigger = $input.first() ? $input.first().json : {};

// Bezpečně získáme text
const text = String(trigger.text || '').trim();

// Pokud je text prázdný (což při přímém testu sub-workflow bude), použijeme default
const prompt = text && text.length > 0
  ? `Search the web for: "${text}". Return top 5 results with titles and absolute URLs. If the user provided a specific URL, fetch it and return key facts.`
  : 'Search the web for recent relevant results. Return top 5 results with titles and absolute URLs.';

return [{
  json: {
    // Pokud chat_id neexistuje, dáme tam null nebo placeholder, aby to nepadlo
    chat_id: trigger.chat_id || null,
    conversation_id: trigger.conversation_id || null,
    run_id: trigger.run_id || null,
    prompt: prompt,
    model: trigger.model || 'openclaw:main'
  }
}];

const trigger = $('Execute Workflow Trigger').first().json;
const content = ($json.output?.[0]?.content || []).map(c => c.text || '').join('\n');
const output = content || JSON.stringify($json);

return [{
  json: {
    output: output.slice(0, 4000),
    chat_id: trigger.chat_id,
    conversation_id: trigger.conversation_id,
    run_id: trigger.run_id
  }
}];
