const trigger = $input.first()?.json ?? {};

const text = String(trigger.text ?? '').trim();
const safeQuery = text.replace(/\s+/g, ' ').trim();

const hardRules = [
  'NEVER use or mention: curl, scraping tool, sandbox limitations, nemám přístup k webu.',
  'NEVER output generic troubleshooting like: \"Zkontroluj, zda je spuštěná brána OpenClaw\" or \"openclaw gateway start\".',
  'If the browser tool is unavailable in your environment/policy, output EXACTLY: BROWSER_TOOL_UNAVAILABLE (no other text).',
];

const strictToolingRules = [
  'Use ONLY the built-in browser navigation (open pages, click, scroll, read).',
  'Do NOT use exec/shell, python, pip, Botasaurus, web-utils, scripts, or any scraping code.',
  'If a page blocks access, report what you can see and stop.',
];

const tools = [
  'You MUST use the browser tool (OpenClaw-managed browser).',
  'Do NOT use web_search.',
];

const outputFormat = [
  'Return the top 5 results. For each: title, absolute URL, and 1-sentence Czech summary.',
  'Reply in Czech.',
  'If you cannot browse because browser tool is unavailable, return exactly: BROWSER_TOOL_UNAVAILABLE.',
];

const task = safeQuery
  ? [
      `Find current information for: ${safeQuery}.`,
      'If you need to search, use browser to open a public search results page (e.g. DuckDuckGo/Google) and extract results from the page.',
    ]
  : [
      'Find 5 recent relevant results.',
      'If you need to search, use browser to open a public search results page (e.g. DuckDuckGo/Google).',
    ];

const sections = [
  'You are Jackie (web handler).',
  '',
  'HARD RULES',
  ...hardRules.map(r => `- ${r}`),
  '',
  'STRICT TOOLING RULES:',
  ...strictToolingRules.map(r => `- ${r}`),
  '',
  'TOOLS',
  ...tools.map(r => `- ${r}`),
  '',
  'TASK',
  ...task,
  '',
  'OUTPUT',
  ...outputFormat,
];

const prompt = sections.join('\n');

return [
  {
    json: {
      chat_id: trigger.chat_id ?? null,
      conversation_id: trigger.conversation_id ?? null,
      run_id: trigger.run_id ?? null,
      prompt,
      model: trigger.model ?? 'openclaw:main',
    },
  },
];

